import { Player } from "textalive-app-api";

const audioElement = document.getElementById("audioElement");
const videoCanvas = document.getElementById('videoCanvas');
const ctx = videoCanvas.getContext('2d');
const playPauseBtn = document.getElementById('playPauseBtn');
const generateBtn = document.getElementById('generateBtn');

let particles = [];

const player = new Player({
    app: { token: "6OdzAkUG3Vc5B9nJ" },
    mediaElement: audioElement
});

player.addListener({
    onAppReady(app) {
        if (!app.managed) {
            player.createFromSongUrl("https://piapro.jp/t/YW_d/20210206123357");
        }
    },
    onVideoReady(video) {
        console.log("楽曲情報:", video);
        createMusicVideo();
    },
    onTimerReady() {
        console.log("音楽データ準備完了");
    },
    onTimeUpdate(position) {
        drawFrame(position);
    },
    onPlay() {
        console.log("再生開始");
    },
    onPause() {
        console.log("一時停止");
    },
    onStop() {
        console.log("停止");
    },
    onError(e) {
        console.error("エラー発生:", e);
    }
});

function createMusicVideo() {
    // キャンバスサイズの設定
    videoCanvas.width = window.innerWidth;
    videoCanvas.height = window.innerHeight;

    // アニメーションの開始
    requestAnimationFrame(animate);
}

function drawFrame(position) {
    ctx.clearRect(0, 0, videoCanvas.width, videoCanvas.height);

    // 背景のグラデーション
    const gradient = ctx.createLinearGradient(0, 0, videoCanvas.width, videoCanvas.height);
    gradient.addColorStop(0, "rgba(0, 0, 50, 0.5)");
    gradient.addColorStop(1, "rgba(50, 0, 50, 0.5)");
    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, videoCanvas.width, videoCanvas.height);

    // ビートに合わせて円を描画
    const beat = player.findBeat(position);
    if (beat) {
        const radius = 100 + Math.sin(beat.index * 0.2) * 20;
        ctx.beginPath();
        ctx.arc(videoCanvas.width / 2, videoCanvas.height / 2, radius, 0, Math.PI * 2);
        ctx.strokeStyle = "rgba(255, 255, 255, 0.5)";
        ctx.lineWidth = 5;
        ctx.stroke();
    }

    // パーティクルの更新と描画
    updateParticles();

    // 歌詞の表示
    displayLyrics(position);
}

function animate() {
    drawFrame(player.position);
    requestAnimationFrame(animate);
}

function updateParticles() {
    // 新しいパーティクルの追加
    if (player.isPlaying && Math.random() < 0.1) {
        particles.push({
            x: Math.random() * videoCanvas.width,
            y: videoCanvas.height,
            size: Math.random() * 5 + 2,
            color: `hsl(${Math.random() * 360}, 100%, 50%)`,
            speedY: -Math.random() * 2 - 1
        });
    }

    // パーティクルの更新と描画
    particles.forEach((p, index) => {
        p.y += p.speedY;
        ctx.fillStyle = p.color;
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.size, 0, Math.PI * 2);
        ctx.fill();

        // 画面外に出たパーティクルを削除
        if (p.y + p.size < 0) {
            particles.splice(index, 1);
        }
    });
}

function displayLyrics(position) {
    const phrase = player.video.getPhraseAt(position);
    if (phrase) {
        ctx.fillStyle = "#FFFFFF";
        ctx.font = "bold 24px Arial";
        ctx.textAlign = "center";
        ctx.textBaseline = "bottom";

        // 歌詞を下部に表示
        ctx.fillText(phrase.text, videoCanvas.width / 2, videoCanvas.height - 30);

        // 次の歌詞を薄く表示
        const nextPhrase = player.video.getPhraseAfter(position);
        if (nextPhrase) {
            ctx.fillStyle = "rgba(255, 255, 255, 0.5)";
            ctx.font = "20px Arial";
            ctx.fillText(nextPhrase.text, videoCanvas.width / 2, videoCanvas.height - 60);
        }
    }
}

playPauseBtn.addEventListener("click", () => {
    if (player.isPlaying) {
        player.requestPause();
    } else {
        player.requestPlay();
        audioElement.play().catch(e => console.error("音声再生エラー:", e));
    }
});

generateBtn.addEventListener("click", () => {
    if (player.video) {
        createMusicVideo();
    }
});

window.addEventListener('resize', () => {
    videoCanvas.width = window.innerWidth;
    videoCanvas.height = window.innerHeight;
});

// 初期化
window.onload = () => {
    console.log("ページ読み込み完了");
    player.volume = 50; // 音量を50%に設定
};
