import { Player } from "textalive-app-api";
import * as THREE from 'three';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader.js';

// TextAlive Playerオブジェクトを作成
const player = new Player({
  app: {
    token: "rR1JoTmnx0KeK0Wn",
  },
  mediaElement: document.createElement("audio"),
});

const playButton = document.getElementById("playButton");
const canvasContainer = document.getElementById("canvas-container");

// Three.jsのセットアップ
const scene = new THREE.Scene();
const camera = new THREE.PerspectiveCamera(75, window.innerWidth / (window.innerHeight / 2), 0.1, 1000);
const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
renderer.setSize(window.innerWidth, window.innerHeight / 2);
canvasContainer.appendChild(renderer.domElement);

// フォントローダー
const loader = new THREE.FontLoader();
let font;
loader.load('https://threejs-plactice.vercel.app/fontloader/fonts/helvetiker_regular.typeface.json', function(loadedFont) {
  font = loadedFont;
  console.log("フォントが正しく読み込まれました:", font);

  // フォントがロードされた後にプレイヤーを初期化
  initializePlayer();
}, undefined, function(error) {
  console.error("フォントの読み込みに失敗しました:", error);
});

let lyricMeshes = [];

// 中央に表示するアセット
const geometry = new THREE.BoxGeometry(1, 1, 1);
const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
const centralObject = new THREE.Mesh(geometry, material);
centralObject.position.set(0, 0, 0);
scene.add(centralObject);

function createLyricMesh(text) {
  if (!font) return;

  const textGeometry = new THREE.TextGeometry(text, {
    font: font,
    size: 0.5,
    height: 0.1,
    curveSegments: 12,
    bevelEnabled: false,
  });
  const textMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
  const textMesh = new THREE.Mesh(textGeometry, textMaterial);
  textMesh.position.set(-2, 3, 0);  // 歌詞の初期位置を中央オブジェクトの上に設定
  lyricMeshes.push(textMesh);
  scene.add(textMesh);
  return textMesh;
}

camera.position.z = 5;

const animate = function () {
  requestAnimationFrame(animate);

  // 歌詞を降らせるアニメーション
  lyricMeshes.forEach(mesh => {
    mesh.position.y -= 0.02;  // 歌詞メッシュを少しずつ下に移動
    if (mesh.position.y < -5) {  // メッシュが画面の下に出たら削除
      scene.remove(mesh);
    }
  });
  lyricMeshes = lyricMeshes.filter(mesh => mesh.position.y >= -5);

  renderer.render(scene, camera);
};

animate();

// 視点操作用のボタン
const rotateLeftButton = document.getElementById("rotateLeft");
const rotateRightButton = document.getElementById("rotateRight");
const rotateUpButton = document.getElementById("rotateUp");
const rotateDownButton = document.getElementById("rotateDown");

rotateLeftButton.addEventListener("click", () => {
  camera.rotation.y += 0.1;
});

rotateRightButton.addEventListener("click", () => {
  camera.rotation.y -= 0.1;
});

rotateUpButton.addEventListener("click", () => {
  camera.rotation.x += 0.1;
});

rotateDownButton.addEventListener("click", () => {
  camera.rotation.x -= 0.1;
});

// プレイヤーを初期化する関数
function initializePlayer() {
  // 楽曲が読み込まれたら、ボタンを有効にする
  player.addListener({
    onAppReady: (app) => {
      if (!app.managed) {
        player.createFromSongUrl("https://piapro.jp/t/YW_d/20210206123357");
      }
    },
    onVideoReady: (v) => {
      playButton.disabled = false;
    },
    onTimeUpdate: (position) => {
      if (player.video && font) {
        const now = player.timer.position;
        const char = player.video.findChar(now);
        if (char && char.startTime <= now && char.endTime > now) {
          const text = char.parent.text;
          const ruby = char.parent.parent.ruby;
          const displayedText = ruby ? ruby.text : text;

          // 歌詞メッシュを作成して追加
          createLyricMesh(displayedText);
        }
      }
    }
  });

  // ボタンをクリックしたときに音楽を再生
  playButton.addEventListener("click", () => {
    if (player) {
      if (player.isPlaying) {
        player.requestPause();
        playButton.textContent = "再生";
      } else {
        player.requestPlay();
        playButton.textContent = "一時停止";
      }
    }
  });

  // ウィンドウリサイズ時にレンダラーとカメラの調整
  window.addEventListener('resize', () => {
    const width = window.innerWidth;
    const height = window.innerHeight / 2;
    renderer.setSize(width, height);
    camera.aspect = width / height;
    camera.updateProjectionMatrix();
  });
}
